mapper.update:
update board
set BOARD_TITLE=#{adopTitle}, BOARD_CONTENT=#{adopContent}
where BOARD_NO=#{adopId}

update FILE_INFO
set FILE_STATUS='N'
where FILE_NO=#{fileNo}

UPDATE COMMENTS
SET COMMETS_COTENT=#{rContent}
WHERE COMMENTS_NO=#{rId}

update COMMENTS
set COMMETS_STATUS='N'
where COMMENTS_NO=#{rId}

update animal_info
set animal_STATUS='N'
where animal_no = #{ animalNo }

update ANIMAL_INFO
set ANIMAL_STATUS='Y'
where ANIMAL_NO=#{animalNo}
