mapper.update:
update board
set BOARD_VIEW = BOARD_VIEW + 1
where BOARD_NO = #{volId}

mapper.select:
select *
from FILE_INFO
WHERE FILE_STATUS='Y' and BOARD_NO = #{boardNo}

mapper.update:
update board
set BOARD_TITLE=#{volTitle}, BOARD_CONTENT=#{volContent}, CATE_NO=#{volCateNo}
where BOARD_NO=#{volId}

update FILE_INFO
set FILE_STATUS='N'
where FILE_NO=#{fileNo}

update board
set BOARD_STATUS='N'
where BOARD_NO=#{volId}
